function iconCaption($url = "", $caption = "", $iconName = "", $class = "", $iconClass = "", $iconSize = "")
{
  $out = "";
  
  $iconName = $iconName ?: "arrow";
  $iconClass = $iconClass ?: "rounded-circle p-2 me-2 ms-n1 border text-gray";
  $icon = icon($iconName, $iconSize, "d-inline-flex $iconClass");

  if ($url) {
    $out .= "<a class=\"$class me-2 d-inline-flex align-items-center text-nowrap\" title=\"$caption\" href=\"$url\">{$icon}{$caption}</a>";
  }

  return $out;
}
